  watch:{
    '$store.state.pattern.matchedSim3'(newValue, oldValue) {
      let _this = this
      console.log("resultChart",newValue)//获得了当前的匹配的id
      let sim3 = newValue
      let sim3Arr=[]
      sim3 = sim3.split("[")[1]
      sim3 = sim3.split("]")[0]
      sim3 = sim3.split(",")
      sim3Arr.push(sim3[0])
      sim3Arr.push(sim3[1].split(" ")[1])
      sim3Arr.push(sim3[2].split(" ")[1])
      console.log("sim3",sim3Arr)
      _this.currentChartParam.currentLegend = sim3Arr
      postRequestJson('/floodInfo/match',sim3Arr)
        .then(async (res)=>{
          // console.log(newValue,res)
          let data = res.data.data;
          console.log("data",data)
          let flowArr=[]
          for (let i = 0; i < data.length; i++) {
            let flow = data[i].flow;
            let id = data[i].floodID
            /**
             * 现在的id是排序过的... 1,12,16
             * 实际上第一个id是。。。12,16,1
             */
            let formatFlow = str2listForRain(flow);
            for (let j=0;j<3;j++){
              //当前id=2，
              if(id==sim3Arr[j]){
                flowArr[j] = formatFlow
                console.log("index",j)
              }
            }
            // flowArr.push(formatFlow)
          }
          console.log("flowArr",flowArr)//得到top3的流量特征，绑定要图表上

          _this.currentChartParam.flow1 = flowArr[0]
          _this.currentChartParam.flow2 = flowArr[1]
          _this.currentChartParam.flow3 = flowArr[2]
          _this.currentChartParam.preFloodIds=sim3Arr
          // console.log(numbers)
          // console.log("store.matchID",store.matchID)
          await postRequest('/floodInfo',{"floodID":_this.$store.state.pattern.matchID})
            .then((res)=>{
              let data = res.data.data
              let currentFlow = str2listForRain(data.flow)
              _this.currentChartParam.flow = currentFlow
              // store.changeCurrent(currentFlow)

              _this.initChart()
            })
            .catch((err)=>{

            })
        })
        .catch((err)=>{

        })
    }


